Get-ADUser -Filter { Enabled -eq $true } -Properties Title,Department,Manager -SearchBase "OU=Users,DC=MSXTEC,DC=co,DC=uk" -SearchScope Subtree | select Name, Title, Department, @{n="ManagerName";e={get-aduser $_.manager | select -ExpandProperty name}}, @{n="ManagerMail";e={get-aduser $_.manager -properties mail | select -ExpandProperty mail}} | export-csv -path "c:\msx\export.csv"
